mapper.insert:
INSERT INTO tbl_board(   boardNumber 
					      , boardTitle  
					      , boardContent
					      , userId
					      , categoryCode  )
              VALUES(    tbl_board_seq.NEXTVAL 
              		  , #{boardTitle}
              		  , #{boardContent}
              		  , #{userId}
              		  , #{categoryCode}  )

mapper.update:
UPDATE tbl_board
   SET boardTitle    =   #{boardTitle},
   	   boardContent  =   #{boardContent}
 WHERE boardNumber = #{boardNumber} AND userId=#{userId}

mapper.delete:
DELETE 
  FROM tbl_board
 WHERE boardNumber = #{boardNumber} AND userId=#{userId}

mapper.select:
SELECT	userId
FROM tbl_board
WHERE boardNumber = #{boardNumber}

mapper.update:
UPDATE tbl_board 
SET boardHit = boardHit+1
WHERE boardNumber = #{boardNumber}
